import React, {useState, useEffect} from 'react';
import { useParams, Link } from 'react-router-dom';
import { Slide } from 'react-slideshow-image';
import { PageArea, Fake, OthersArea, BreadChumb } from './styled';
import useApi from '../../helpers/OlxAPI';
import "react-slideshow-image/dist/styles.css"


import { PageContainer } from '../../components/MainComponents';
import AdItem from '../../components/partials/AdItem'

const Page = () => {

    const api = useApi();
    const { id } = useParams();

    const [loading, setLoading] = useState(true);
    const [adInfo, setAdInfo] = useState({});

    useEffect(()=>{
        const getAdInfo = async (id) => {
            const json = await api.getAd(id, true);
            setAdInfo(json);
            setLoading(false);
        }
        getAdInfo(id);
    },[]);
    
    const formateDate = (date) =>{
        let cDate = new Date(date);
        let months = ['janeiro','fevereiro','março','abril','maio','junho','julho','agosto','setembro','outubro','novembro','dezembro'];
        let cDay = cDate.getDate();
        let CMonth = cDate.getMonth();
        let cYear = cDate.getFullYear();

        return `${cDay} de ${months[CMonth]} de ${cYear}`;
    }
    
    return (
        <PageContainer>
                {adInfo.category &&
                <BreadChumb>
                    Você está aqui: 
                    <Link to="/"> Home</Link>
                    /
                    <Link to={`/ads?state=${adInfo.stateName}`}>{adInfo.stateName}</Link>
                    /
                    <Link to={`/ads?state=${adInfo.stateName}&cat=${adInfo.category.slug}`}>{adInfo.category.name}</Link>
                    / {adInfo.title}
                </BreadChumb>
                }
                <PageArea>
                    <div className="leftSide">
                        <div className="box">
                            <div className="adImage">
                                {loading && <Fake height={300}/>}
                                {adInfo.images &&
                                    <Slide>
                                        {adInfo.images.map((img, k) =>
                                            <div key={k} className="each-slide">
                                                <img src={img} alt="" />
                                            </div>
                                        )}
                                    </Slide>
                                }
                            </div>
                            <div className="adInfo">
                                <div className="adName">
                                        {loading && <Fake height={20}/>}
                                        {adInfo.title &&
                                            <h2>{adInfo.title}</h2>
                                        }
                                        {adInfo.dateCreated &&
                                            <small>Criado em: {formateDate(adInfo.dateCreated)}</small>
                                        }
                                </div>                            
                                <div className="adDescription">
                                        {loading && <Fake height={100}/>}
                                        {adInfo.description}
                                        <hr/>
                                        {adInfo.views &&
                                            <small>Visto: {adInfo.views} vezes</small>

                                        }
                                </div>
                            </div>    
                        </div>    
                    </div>
                    <div className="rightSide">
                        <div className="box box--padding">
                            {loading && <Fake height={20}/>}
                            {adInfo.priceNegotiable &&
                                "Preço Negociável"
                            }
                            {!adInfo.priceNegotiable && adInfo.price &&
                            <div className="price"> Preço: <span>R$ {adInfo.price}</span> </div>

                            }
                        </div> 
                        {loading && <Fake height={50}/>}
                        {adInfo.userInfo &&
                            <>
                                <a href={`mailto:${adInfo.userInfo.email}`} target="_blank" className="contactSellerLink"> Fale com o vendedor</a>
                                <div className="createdby box box--padding">
                                    <strong>{adInfo.userInfo.name}</strong>
                                    <small>E-mail: {adInfo.userInfo.email}</small>
                                    <small>Estado: {adInfo.stateName}</small>
                                </div>
                            </>
                        }
                            
                    </div>
                       
                </PageArea>

                <OthersArea>
                    {adInfo.others &&
                        <>
                            <h2>Outras ofertas deste vendedor:</h2>
                            <div className="list">
                                {adInfo.others.map((i,k)=>
                                    <AdItem key={k} data={i}/>
                                )}
                            </div>
                        </>
                    }
            </OthersArea>
        </PageContainer>

    );
}

export default Page;
